    //=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=
    //Constructor
    public LevelManager(Game game) {
        this.game = game;
        buildAllLevel();
    }

    //=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=--=
    //Build level
    private void buildAllLevel() {
        levels.add(level1);
        levels.add(level2);
    }

    //=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=
    //Draw the level
    public void draw (Graphics g){

    }

    //Update the level
    public void update() {

    }

    //=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=
    //load next level
    public void loadNextLevel() {
        levelIndex++;
        if (levelIndex >= levels.size()) {
            levelIndex = 0;
            System.out.println("no level");
            Gamestate.currentState = Gamestate.MENU;
        }

        System.out.println("level index: " + levelIndex);
        System.out.println("levels size: " + levels.size());


        int[][] newLevel = levels.get(levelIndex);
        game.getPlaying().getEnemyManager().loadEnemies(newLevel);
        game.getPlaying().getPlayer().getLevelData(newLevel);
        game.getPlaying().getObjectManager().loadObject(newLevel);
        game.getPlaying().setMaxLevelOffsetX(Game.TILE_SIZE_SCALE * (130 - Game.TILE_WIDTH));
    }


    //=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=
    //Get level 1
    public int[][] getLevel1() {
        return level1;
    }

    public int[][] getLevel2() {
        return level2;
    }

    public int getLevelIndex() {
        return levelIndex;
    }

    public int[][] getCurrentLevel() {
        return levels.get(levelIndex);
    }

    public int getAmountOfLevels() {
        return levels.size();
    }

